mapper.select:
select order_status
from helper_plus_order
where order_id = #{orderId}
  and order_initiator_id = #{orderInitiatorId}
  and is_deleted = 0

mapper.update:
update helper_plus_order
set is_deleted = 1,
    update_gmt = CURRENT_TIMESTAMP
where order_id = #{orderId}
  and order_initiator_id = #{orderInitiatorId}
  and is_deleted = 0

update helper_plus_order
set order_status = 5,
    update_gmt = CURRENT_TIMESTAMP
where order_id = #{orderId}
  and order_initiator_id = #{orderInitiatorId}
  and is_deleted = 0
